for ion in `ls -d */DDEC* | awk -F/ '{print $1}' | uniq`; do for functional in m062x; do for basis in `ls -d */DDEC* | awk -F/ '{print $2}' | awk -F_ '{print $3}' | sort | uniq`; do charge=`grep Charge $ion/DDEC6_m062x_aug-cc-pvdz/"$ion"_m062x_aug-cc-pvdz_DDEC6.out | head -n1 | awk '{print $3}'`; multiplicity=`grep Multiplicity $ion/DDEC6_m062x_aug-cc-pvdz/"$ion"_m062x_aug-cc-pvdz_DDEC6.out | head -n1 | awk '{print $6}'`; ./MCLF_Disp.sh $ion $functional $basis $charge $multiplicity 4 16gb; done; done; done
